 }
}

@Composable
fun ThemeRadioButton(
 label: String,
 mode: AppThemeMode,
 selected: AppThemeMode,
 onSelected: (AppThemeMode) -> Unit
) {
 Row(verticalAlignment = Alignment.CenterVertically) {
 RadioButton(
 selected = (selected == mode),
 onClick = { onSelected(mode) }
 )
 Text(label)
 }
}

